using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Management;
using System.Security.Cryptography;

namespace RedEyeEngine
{
    public class EngineRSA
    {
        string privatekey = "<RSAKeyValue><Modulus></Modulus><Exponent>AQAB</Exponent><P></P><Q></Q><DP></DP><DQ></DQ><InverseQ></InverseQ><D></D></RSAKeyValue>";

        public string Decrypt(string Data)
        {
            RSACryptoServiceProvider rsa = new RSACryptoServiceProvider();
            rsa.FromXmlString(privatekey);

            return System.Text.Encoding.UTF8.GetString(rsa.Decrypt(System.Convert.FromBase64String(Data), true));
        }
    }
}
